import React, { Component, Fragment, useEffect } from 'react';
import isEmpty from 'lodash.isempty';
import router from "next/router"
import auth0 from "../lib/auth0"
import { db } from "../lib/db"
import GoogleMapReact from 'google-map-react';
function createMapOptions(maps) {
  return {
    zoomControlOptions: {
      position: maps.ControlPosition.RIGHT_CENTER,
      style: maps.ZoomControlStyle.SMALL
    },
    mapTypeControlOptions: {
      position: maps.ControlPosition.TOP_RIGHT
    },
    mapTypeControl: true
  };
}

const App = (props) => {
  useEffect(() => {
    if (!props.isAuth) router.push("/")
    else if (props.forceCreate) router.push("/create-status")
  })

  const defaultProps = {
    center: [props.user.latitude, props.user.longitude],
    zoom: 9,
  };

  if (!props.isAuth || props.forceCreate) return null
  return (
    <div>
      <h1 className='font-bold text-4xl py-8'>Status próximos a você:</h1>

      <GoogleMapReact
        defaultCenter={defaultProps.center}
        yesIWantToUseGoogleMapApiInternals
        options={createMapOptions}
        zoom={defaultProps.zoom}
        >
          {props.checkins.map((checkin) => (
            <MyGreatPlace
              key={checkin.id}
              lat={checkin.coords.latitude}
              lng={checkin.coords.longitude}
              text={checkin.status}
            />
          ))}
      </GoogleMapReact>
    </div>
  )
}

export default App

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req)
  const currentDate = new Date().toISOString().substr(0, 10)
  let checkinsList = []
  let props = { user: {}, isAuth: false, forceCreate: false, checkins: [] }
  if (session) {
    const { user } = session
    const todaysCheckin = await db
      .collection("markers")
      .doc(currentDate)
      .collection("checks")
      .doc(user.sub)
      .get()
    
    let forceCreate = true

    if (todaysCheckin.data()) {
      const { coordinates } = todaysCheckin.data()
      forceCreate = false
      const { docs } = await db
        .collection("markers")
        .doc(currentDate)
        .collection("checks")
        .near({
          center: coordinates,
          radius: 18000,
        })
        .get()

      checkinsList = docs.map((doc) => {
        const { status, coordinates } = doc.data()
        const { latitude, longitude } = coordinates 

        return {
          id: doc.id,
          status,
          coords: { latitude, longitude },
        }
      })
    }
    props = { user, isAuth: true, forceCreate, checkins: checkinsList }
  }
  return { props }
}
